import React, {useState} from 'react'
import styled from 'styled-components'
import Posting from '../props/HomePosting'
import Grid from '@material-ui/core/Grid'

function Home() {
  const [searchInput,setSearchInput] = useState("")
  const handleChange =(e)=>{
    setSearchInput(e.target.value)
  }
  const postings = [{"id": 1, "title": "Car"}, {"id": 2, "title": "Bike"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"}]

  return (
  <Background>
    <Header>
      <input
      style={{width: "60vmin" }}
      type="text"
      placeholder = "Search here"
      onChange = {handleChange}
      value = {searchInput}
      />
    </Header>
    <Line/>
    <PostingWrapper>
    <Grid container spacing={4}> 
    {postings.map((post)=>(
      <Grid item sm={4}>
          <Posting Title={post.title}/>
          </Grid>    
    ))}
      </Grid>
    </PostingWrapper>
  </Background>
  );
}

export default Home;

const Background = styled.section`
background-color: #282c34;
min-height: 100vh;
display: flex;
flex-direction: column;
align-items:center;
font-size: calc(10px + 2vmin);
`

const Header = styled.section`
margin-top: 5vh;
`
const Line = styled.section`
background-color:black;
margin-top: 2vh;
height:2px;
`
// const Text = styled.p``

const PostingWrapper = styled.section`
padding: 20px;
width:50%;
border: 1px solid red;
`
