import React from 'react'
import styled from 'styled-components'
import Posting from '../props/HomePosting'
import Grid from '@material-ui/core/Grid'
import SearchBar from '../components/SearchBar'

function Home() {

  const postings = [{"id": 1, "title": "Car"}, {"id": 2, "title": "Bike"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"},{"id": 3, "title": "PC"}]

  return (
  <Background>
    <SearchBar/>
    {/* <Line/> */}
    <PostingWrapper>
    <Grid style={{display: 'flex',alignItems:'center'}} container spacing={4}> 
    {postings.map((post)=>(
      <Grid item sm={4}>
          <Posting Title={post.title}/>
          </Grid>    
    ))}
      </Grid>
    </PostingWrapper>
  </Background>
  );
}

export default Home

const Background = styled.section`
background-color: #282c34;
min-height: 100vh;
display: flex;
flex-direction: column;
font-size: calc(10px + 2vmin);
`


const PostingWrapper = styled.section`
align-self: center;
margin-top:20px;
padding: 20px;
width:50%;
border: 1px solid red;
`
